import React, { useState, useEffect } from 'react';

interface KeyAuthProps {
  onAuthenticated: () => void;
}

const KeyAuth: React.FC<KeyAuthProps> = ({ onAuthenticated }) => {
  const [key, setKey] = useState('');
  const [error, setError] = useState('');
  const [attempts, setAttempts] = useState(0);

  const validKeys = [
    // 从 localStorage 获取有效密钥
    ...JSON.parse(localStorage.getItem('valid_keys') || '[]'),
    'STEALTH' // 更改管理员密钥为 STEALTH
  ];

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (validKeys.includes(key.toUpperCase())) {
      localStorage.setItem('auth_key', key.toUpperCase());
      onAuthenticated();
    } else {
      setError('Invalid key');
      setAttempts(prev => prev + 1);
      if (attempts >= 4) { // 5次尝试后锁定
        localStorage.setItem('locked_until', (Date.now() + 3600000).toString()); // 锁定1小时
      }
    }
  };

  useEffect(() => {
    const lockedUntil = localStorage.getItem('locked_until');
    if (lockedUntil && parseInt(lockedUntil) > Date.now()) {
      setError(`Access locked. Try again in ${Math.ceil((parseInt(lockedUntil) - Date.now()) / 60000)} minutes`);
    }
  }, [attempts]);

  return (
    <div className="key-auth">
      <h2>Enter Access Key</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={key}
          onChange={(e) => {
            setKey(e.target.value);
            setError('');
          }}
          placeholder="Enter your access key"
          className="key-input"
          maxLength={16}
        />
        {error && <p className="error-message">{error}</p>}
        <button 
          type="submit" 
          className="submit-button"
          disabled={!!localStorage.getItem('locked_until') && parseInt(localStorage.getItem('locked_until')!) > Date.now()}
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default KeyAuth;
