import { useState, useCallback, useEffect } from 'react';
import {
  Connection,
  PublicKey,
  ParsedTransactionWithMeta,
  ParsedInstruction,
  PartiallyDecodedInstruction,
} from '@solana/web3.js';

interface TokenInfo {
  address: string;
  symbol: string;
  name: string;
  amount: number;
  marketCap?: number;
  isInput?: boolean;
}

interface Transaction {
  signature: string;
  timestamp: number;
  status: string;
  inputToken?: TokenInfo;
  outputToken?: TokenInfo;
  programName?: string;
}

interface SPLTokenInstruction {
  program: string;
  parsed: {
    type: string;
    info: {
      mint: string;
      tokenAmount: {
        uiAmount: number;
      };
    };
  };
}

interface TransactionMonitorProps {
  programId: string;
}

const getProgramName = (instruction: ParsedInstruction | PartiallyDecodedInstruction): string => {
  if ('program' in instruction) {
    return instruction.program;
  }
  if ('programId' in instruction) {
    return instruction.programId.toString();
  }
  return 'Unknown';
};

const getTokenMarketCap = async (mintAddress: string) => {
  try {
    console.log('Fetching market cap from Pumpfun for:', mintAddress);
    const response = await fetch(`https://api.pumpfun.com/v1/market/token/${mintAddress}`);
    const data = await response.json();
    console.log('Pumpfun API response:', data);
    
    if (data?.success && data?.data) {
      const marketCap = data.data.marketCap;
      console.log('Market cap:', marketCap);
      return marketCap;
    }
  } catch (error) {
    console.error('Error fetching market cap:', error);
  }
  return undefined;
};

const getTokenInfo = async (mintAddress: string) => {
  try {
    console.log('Fetching token info for:', mintAddress);
    const [tokenData, marketCap] = await Promise.all([
      fetch('https://api.helius.xyz/v0/token-metadata?api-key=426605eb-af73-410a-af26-0dc37714935f', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          mintAccounts: [mintAddress],
          includeOffChain: true,
          disableCache: false
        })
      }).then(res => res.json()),
      getTokenMarketCap(mintAddress)
    ]);
    
    console.log('Token metadata:', tokenData);
    console.log('Market cap:', marketCap);

    if (tokenData && tokenData[0]) {
      const tokenInfo = {
        address: mintAddress,
        symbol: tokenData[0].symbol || 'Unknown',
        name: tokenData[0].name || 'Unknown Token',
        amount: 0,
        marketCap: marketCap
      };
      console.log('Created token info:', tokenInfo);
      return tokenInfo;
    }
  } catch (error) {
    console.error('Error fetching token info:', error);
  }
  return null;
};

const TransactionMonitor: React.FC<TransactionMonitorProps> = ({ programId }) => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(false);
  const [processedSignatures] = useState<Set<string>>(new Set());
  const [uniqueTokens, setUniqueTokens] = useState<Set<string>>(new Set());
  const [autoRefreshInterval, setAutoRefreshInterval] = useState<NodeJS.Timeout | null>(null);

  const cleanup = () => {
    if (autoRefreshInterval) {
      clearInterval(autoRefreshInterval);
    }
  };

  useEffect(() => {
    return cleanup;
  }, []);

  useEffect(() => {
    cleanup();
    setTransactions([]);
    processedSignatures.clear();
    setUniqueTokens(new Set());

    monitorTransactions();

    const interval = setInterval(() => {
      if (!loading) {
        monitorTransactions();
      }
    }, 10000);

    setAutoRefreshInterval(interval);

    return cleanup;
  }, [programId]);

  const monitorTransactions = useCallback(async () => {
    if (loading) return;
    
    try {
      setLoading(true);
      console.log('Starting transaction monitoring for program:', programId);

      const connection = new Connection(
        'https://mainnet.helius-rpc.com/?api-key=426605eb-af73-410a-af26-0dc37714935f',
        'confirmed'
      );

      const programPublicKey = new PublicKey(programId);
      
      console.log('Fetching signatures...');
      const signatures = await connection.getSignaturesForAddress(
        programPublicKey,
        { limit: 10 }
      );

      console.log('Found signatures:', signatures.length);

      const newSignatures = signatures.filter(sig => !processedSignatures.has(sig.signature));
      
      if (newSignatures.length === 0) {
        setLoading(false);
        return;
      }

      const delay = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));
      const uniqueTxMap = new Map<string, Transaction>();

      for (const sig of newSignatures) {
        try {
          console.log('Processing signature:', sig.signature);
          await delay(500);
          const tx = await connection.getParsedTransaction(
            sig.signature,
            {
              maxSupportedTransactionVersion: 0,
              commitment: 'confirmed'
            }
          );
          
          if (tx?.meta && tx.blockTime) {
            console.log('Transaction found:', sig.signature);
            
            const preTokenBalances = tx.meta.preTokenBalances || [];
            const postTokenBalances = tx.meta.postTokenBalances || [];
            
            console.log('Pre-token balances:', preTokenBalances);
            console.log('Post-token balances:', postTokenBalances);

            const changedTokens = new Set([
              ...preTokenBalances.map(b => b.mint),
              ...postTokenBalances.map(b => b.mint)
            ]);

            console.log('Changed tokens:', changedTokens);

            const tokenInfoCache: { [key: string]: TokenInfo | null } = {};

            const tokenPromises = Array.from(changedTokens).map(async mint => {
              if (!mint) return null;
              
              if (tokenInfoCache[mint]) {
                return tokenInfoCache[mint];
              }

              const tokenInfo = await getTokenInfo(mint);
              if (tokenInfo) {
                tokenInfoCache[mint] = tokenInfo;
              }
              return tokenInfo;
            });

            const tokens = (await Promise.all(tokenPromises))
              .filter((t): t is TokenInfo => t !== null)
              .map(token => {
                const preBal = preTokenBalances.find(b => b.mint === token.address)?.uiTokenAmount.uiAmount || 0;
                const postBal = postTokenBalances.find(b => b.mint === token.address)?.uiTokenAmount.uiAmount || 0;
                const change = postBal - preBal;
                
                return {
                  ...token,
                  amount: Math.abs(change),
                  isInput: change < 0
                };
              });

            if (tokens.length > 0) {
              const inputTokens = tokens.filter(t => t.isInput);
              const outputTokens = tokens.filter(t => !t.isInput);

              const newTx: Transaction = {
                signature: sig.signature,
                timestamp: tx.blockTime,
                status: tx.meta?.err ? 'Failed' : 'Success',
                inputToken: inputTokens[0],
                outputToken: outputTokens[0],
                programName: getProgramName(tx.transaction.message.instructions[0])
              };

              if (!uniqueTxMap.has(sig.signature)) {
                uniqueTxMap.set(sig.signature, newTx);
                processedSignatures.add(sig.signature);
              }
            }
          }
        } catch (error) {
          console.error('Error processing transaction:', sig.signature, error);
        }
      }

      if (uniqueTxMap.size > 0) {
        setTransactions(prevTxs => {
          const existingSignatures = new Set(prevTxs.map(tx => tx.signature));
          const newTxs = Array.from(uniqueTxMap.values()).filter(
            tx => !existingSignatures.has(tx.signature)
          );
          return [...newTxs, ...prevTxs];
        });
      }
    } catch (error) {
      console.error('Error in monitorTransactions:', error);
    } finally {
      setLoading(false);
    }
  }, [programId, processedSignatures, loading]);

  const openPhotonLink = (tokenAddress: string) => {
    window.open(`https://photon-sol.tinyastro.io/en/lp/${tokenAddress}`, '_blank');
  };

  return (
    <div className="transaction-monitor">
      <div className="monitor-header">
        <div className="title-section">
          <h2>Transaction Monitor</h2>
          <span className="subtitle">Real-time Solana transaction tracking</span>
        </div>
        <div className="refresh-container">
          <button 
            onClick={monitorTransactions} 
            disabled={loading}
            className="refresh-button"
          >
            {loading ? (
              <>
                <span className="loading-spinner"></span>
                Loading
              </>
            ) : (
              <>
                <span className="refresh-icon">↻</span>
                Manual Refresh
              </>
            )}
          </button>
        </div>
      </div>
      <div className="transactions-list">
        {transactions.length === 0 ? (
          <div className="no-transactions">
            <p>No transactions found</p>
            <span className="hint">Click refresh to check for new transactions</span>
          </div>
        ) : (
          transactions.map((tx) => (
            <div key={tx.signature} className="transaction-item">
              <div className="transaction-header">
                <div className="timestamp">
                  <span className="label">Time:</span>
                  {new Date(tx.timestamp * 1000).toLocaleString()}
                </div>
                <div className={`status ${tx.status === '成功' ? 'success' : 'failed'}`}>
                  {tx.status === '成功' ? 'Success' : 'Failed'}
                </div>
              </div>
              <div className="swap-info">
                {tx.inputToken && (
                  <div className="token-info input-token">
                    <div className="token-amount">
                      <span className="label">Paid:</span>
                      <span className="amount">{tx.inputToken.amount}</span>
                      <span className="symbol">{tx.inputToken.symbol}</span>
                    </div>
                    {typeof tx.inputToken.marketCap === 'number' && (
                      <div className="market-cap">
                        <span className="label">Market Cap:</span>
                        <span className="value">
                          ${new Intl.NumberFormat('en-US', {
                            notation: 'compact',
                            maximumFractionDigits: 2
                          }).format(tx.inputToken.marketCap)}
                        </span>
                      </div>
                    )}
                    <button 
                      className="photon-link-button"
                      onClick={() => openPhotonLink(tx.inputToken!.address)}
                    >
                      View on Photon LP
                      <span className="address">({tx.inputToken.address})</span>
                    </button>
                  </div>
                )}
                {tx.outputToken && (
                  <div className="token-info output-token">
                    <div className="token-amount">
                      <span className="label">Received:</span>
                      <span className="amount">{tx.outputToken.amount}</span>
                      <span className="symbol">{tx.outputToken.symbol}</span>
                    </div>
                    {typeof tx.outputToken.marketCap === 'number' && (
                      <div className="market-cap">
                        <span className="label">Market Cap:</span>
                        <span className="value">
                          ${new Intl.NumberFormat('en-US', {
                            notation: 'compact',
                            maximumFractionDigits: 2
                          }).format(tx.outputToken.marketCap)}
                        </span>
                      </div>
                    )}
                    <button 
                      className="photon-link-button"
                      onClick={() => openPhotonLink(tx.outputToken!.address)}
                    >
                      View on Photon LP
                      <span className="address">({tx.outputToken.address})</span>
                    </button>
                  </div>
                )}
              </div>
              {tx.programName && (
                <div className="program-name">
                  <span className="label">Program:</span>
                  {tx.programName}
                </div>
              )}
              <div className="signature">
                <span className="label">Transaction Signature:</span>
                <span className="value">{tx.signature}</span>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default TransactionMonitor;
