import { useState, useCallback } from 'react';
import {
  Connection,
  PublicKey,
  ParsedTransactionWithMeta,
} from '@solana/web3.js';

interface TokenInfo {
  address: string;
  symbol: string;
  name: string;
  amount: number;
}

interface Transaction {
  signature: string;
  timestamp: number;
  status: string;
  inputToken?: TokenInfo;
  outputToken?: TokenInfo;
}

interface TransactionMonitorProps {
  programId: string;
}

const TransactionMonitor: React.FC<TransactionMonitorProps> = ({ programId }) => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(false);
  const [processedSignatures] = useState<Set<string>>(new Set());

  const getTokenInfo = async (mintAddress: string) => {
    try {
      const response = await fetch('https://api.helius.xyz/v0/token-metadata?api-key=426605eb-af73-410a-af26-0dc37714935f', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          mintAccounts: [mintAddress],
          includeOffChain: true,
          disableCache: false
        })
      });
      
      const tokenData = await response.json();
      if (tokenData && tokenData[0]) {
        return {
          address: mintAddress,
          symbol: tokenData[0].symbol || 'Unknown',
          name: tokenData[0].name || 'Unknown Token',
          amount: 0 // 将在后面更新
        };
      }
    } catch (error) {
      console.error('Error fetching token info:', error);
    }
    return null;
  };

  const monitorTransactions = useCallback(async () => {
    try {
      setLoading(true);
      const connection = new Connection(
        'https://mainnet.helius-rpc.com/?api-key=426605eb-af73-410a-af26-0dc37714935f',
        'confirmed'
      );

      const programPublicKey = new PublicKey(programId);
      
      const signatures = await connection.getSignaturesForAddress(
        programPublicKey,
        { limit: 10 }
      );

      const newSignatures = signatures.filter(sig => !processedSignatures.has(sig.signature));
      
      if (newSignatures.length === 0) {
        setLoading(false);
        return;
      }

      const delay = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));
      const txs = [];

      for (const sig of newSignatures) {
        try {
          await delay(500);
          const tx = await connection.getParsedTransaction(
            sig.signature,
            {
              maxSupportedTransactionVersion: 0,
              commitment: 'confirmed'
            }
          );
          
          if (tx?.meta && tx.blockTime) {
            let inputToken: TokenInfo | undefined;
            let outputToken: TokenInfo | undefined;

            // 分析交易中的代币转移
            for (const instruction of tx.transaction.message.instructions) {
              if (instruction.program === 'spl-token' && instruction.parsed) {
                const { type, info } = instruction.parsed;
                
                if (type === 'transferChecked' || type === 'transfer') {
                  const tokenInfo = await getTokenInfo(info.mint);
                  if (tokenInfo) {
                    tokenInfo.amount = info.tokenAmount?.uiAmount || 0;
                    
                    // 假设第一个转移是输入代币，第二个是输出代币
                    if (!inputToken) {
                      inputToken = tokenInfo;
                    } else if (!outputToken) {
                      outputToken = tokenInfo;
                    }
                  }
                }
              }
            }

            if (inputToken || outputToken) {
              txs.push({
                signature: sig.signature,
                timestamp: tx.blockTime,
                status: tx.meta?.err ? '失败' : '成功',
                inputToken,
                outputToken
              });
              processedSignatures.add(sig.signature);
            }
          }
        } catch (error) {
          console.error('Error fetching transaction:', sig.signature, error);
        }
      }

      setTransactions(prevTxs => [...txs, ...prevTxs]);
    } catch (error) {
      console.error('Error fetching transactions:', error);
    } finally {
      setLoading(false);
    }
  }, [programId, processedSignatures]);

  return (
    <div className="transaction-monitor">
      <div className="monitor-header">
        <h2>交易监控</h2>
        <button 
          onClick={monitorTransactions} 
          disabled={loading}
          className="refresh-button"
        >
          {loading ? '加载中...' : '刷新'}
        </button>
      </div>
      <div className="transactions-list">
        {transactions.length === 0 ? (
          <p>暂无交易</p>
        ) : (
          transactions.map((tx) => (
            <div key={tx.signature} className="transaction-item">
              <p>时间: {new Date(tx.timestamp * 1000).toLocaleString()}</p>
              <p>状态: {tx.status}</p>
              <div className="swap-info">
                {tx.inputToken && (
                  <p>支付: {tx.inputToken.amount} {tx.inputToken.symbol} ({tx.inputToken.address})</p>
                )}
                {tx.outputToken && (
                  <p>获得: {tx.outputToken.amount} {tx.outputToken.symbol} ({tx.outputToken.address})</p>
                )}
              </div>
              <p className="signature">交易签名: {tx.signature}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default TransactionMonitor;
